import axios from "axios";
import { useEffect, useState } from "react";
 

function UserCRUD() {
 
  const [Id, setId] = useState("");
  const [Name, setName] = useState("");
  const [LastName, setLastName] = useState("");
  const [Email, setEmail] = useState("");
  const [Password, setPassword] = useState("");
  const [Category, setCategory] = useState("");
  const [SubCategory, setSubCategory] = useState("");
  const [Other, setOther] = useState("");
  const [PhoneNumber, setPhoneNumber] = useState("");
  const [Birthdate, setBirthdate] = useState("");

  const [users, setUsers] = useState([]);

    useEffect(() => {
      (async () => await Load())();
    }, []);
    
    async function Load() {
      
      const result = await axios.get("https://localhost:7228/api/User/GetUser");
      setUsers(result.data);
      console.log(result.data);
    }
    async function save(event) {
    
      event.preventDefault();
      try {
        await axios.post("https://localhost:7228/api/User/AddUser", {
        Id: 0,
        Name: Name,
        LastName: LastName,
        Email: Email,
        Password: Password,
        Category: Category,
        SubCategory: SubCategory,
        Other: Other,
        PhoneNumber: PhoneNumber,
        Birthdate: Birthdate
        
        });
        alert("User Registation Successfully");
        setId("");
        setName("");
        setLastName("");
        setEmail("");
        setPassword("");
        setCategory("");
        setSubCategory("");
        setOther("");
        setPhoneNumber("");
        setBirthdate("");
        
      
        Load();
      } catch (err) {
        alert(err);
      }
    }
   
    async function EditUser(users) {
      setName(users.Name);
      setLastName(users.LastName);
      setEmail(users.Email);
      setPassword(users.Password);
      setCategory(users.Category);
      setSubCategory(users.SubCategory);
      setOther(users.Other);
      setPhoneNumber(users.PhoneNumber);
      setBirthdate(users.Birthdate);

    
      setId(users.id);
    }
   
    async function DeleteUser(Id) {
    await axios.delete("https://localhost:7228/api/User/DeleteUser/" + Id);
     alert("User deleted Successfully");
     setId("");
     setName("");
     setLastName("");
     setEmail("");
     setPassword("");
     setCategory("");
     setSubCategory("");
     setOther("");
     setPhoneNumber("");
     setBirthdate("");
     Load();
    }
   
    async function update(event) {
      event.preventDefault();
      try {
   
    await axios.patch("https://localhost:7228/api/User/UpdateUser/"+ users.find((u) => u.Id === Id).Id || Id,
          {
          Id: Id,
          Name: Name,
          LastName: LastName,
          Email: Email,
          Password: Password,
          Category: Category,
          SubCategory: SubCategory,
          Other: Other,
          PhoneNumber: PhoneNumber,
          Birthdate: Birthdate,

   
          }
        );
        alert("Registation Updateddddd");
        setId("");
        setName("");
        setLastName("");
        setEmail("");
        setPassword("");
        setCategory("");
        setSubCategory("");
        setOther("");
        setPhoneNumber("");
        setBirthdate("");

      
        Load();
      } catch (err) {
        alert(err);
      }
    }






    return (
      <div>
        <div>
        <h1>User Details</h1>
      <div class="container mt-4">
        <form>
          <div class="form-group">
          
            <input
              type="text"
              class="form-control"
              id="id"
              hidden
              value={Id}
              onChange={(event) => {
                setId(event.target.value);
              }}
            />
 
            <label>Name</label>
            <input
              type="text"
              class="form-control"
              id="name"
              value={Name}
              onChange={(event) => {
                setName(event.target.value);
              }}
            />
          </div>
          <div class="form-group">
            <label>Email</label>
            <input
              type="text"
              class="form-control"
              id="email"
              value={Email}
              onChange={(event) => {
                setEmail(event.target.value);
              }}
            />
          </div>




          <div>
            <button class="btn btn-primary mt-4" onClick={save}>
              Register
            </button>
            <button class="btn btn-warning mt-4" onClick={update}>
              Update
            </button>
          </div>
        </form>
      </div>
      <br></br>
 
      <table class="table table-dark" align="center">
        <thead>
          <tr>
            <th scope="col">Id</th>
            <th scope="col">Name</th>
            <th scope="col">Last Name</th>
            <th scope="col">Email</th>
            <th scope="col">Password</th>
            <th scope="col">Category</th>
            <th scope="col">Sub Category</th>
            <th scope="col">Other</th>
            <th scope="col">Phone Number</th>
            <th scope="col">Birthdate</th>

        
            <th scope="col">Option</th>
          </tr>
        </thead>
        {users.map(function fn(user) {
          return (
            <tbody>
              <tr>
                <th scope="row">{user.Id} </th>
                <td>{user.Name}</td>
                <td>{user.LastName}</td>
                <td>{user.Email}</td>
                <td>{user.Password}</td>
                <td>{user.Category}</td>
                <td>{user.SubCategory}</td>
                <td>{user.Other}</td>
                <td>{user.PhoneNumber}</td>
                <td>{user.Birthday}</td>
                
                <td>
                  <button
                    type="button"
                    class="btn btn-warning"
                    onClick={() => EditUser(user)}
                  >
                    Edit
                  </button>
                  <button
                    type="button"
                    class="btn btn-danger"
                    onClick={() => DeleteUser(user.Id)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            </tbody>
          );
        })}
      </table>
        
      </div>
      </div>
    );
  }
  
  export default UserCRUD;
