import axios from "axios";
import { useEffect, useState } from "react";
import bcrypt from 'bcryptjs';
 

function UserCRUD() {
 
  const [Id, setId] = useState("");
  const [Name, setName] = useState("");
  const [LastName, setLastName] = useState("");
  const [Email, setEmail] = useState("");
  const [Password, setPassword] = useState("");
  const [Category, setCategory] = useState("");
  const [SubCategory, setSubCategory] = useState("");
  const [Other, setOther] = useState("");
  const [PhoneNumber, setPhoneNumber] = useState("");
  const [Birthdate, setBirthdate] = useState("");

  //0-register, 1-update, 2-login
  var [mode, setMode] = useState(2);

  var [showUser, setShowUser] = useState(false);
  var [currentUser, setCurrentUser] = useState();

  var [users, setUsers] = useState([]);

    useEffect(() => {
      (async () => await Load())();
    }, []);
    
    async function Load() {
      
      const result = await axios.get("https://localhost:7228/api/User/GetUser");
      setUsers(result.data);
      console.log(result.data);
    }
    async function save(event) {
    
      event.preventDefault();
      var validRegex = /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/;
      try {
        if(Email.match(validRegex)){
          const result = await axios.post("https://localhost:7228/api/User/AddUser", {
            name: Name,
            lastName: LastName,
            email: Email,
            password: bcrypt.hashSync(Password,10),
            category: Category,
            subCategory: SubCategory,
            other: Other,
            phoneNumber: PhoneNumber,
            birthdate: Birthdate
            
            });
            if(result.data!=""){
              alert("User added");
            }else{
              alert("Given email already exsts");
            }
        }else{
          alert("Incorrect email form");
        }
        
        
        
        setId("");
        setName("");
        setLastName("");
        setEmail("");
        setPassword("");
        setCategory("");
        setSubCategory("");
        setOther("");
        setPhoneNumber("");
        setBirthdate("");
        
      
        Load();
      } catch (err) {
        alert(err);
      }
    }
   
    async function EditUser(users) {
      setName(users.name);
      setLastName(users.lastName);
      setEmail(users.email);
      setPassword(users.password);
      setCategory(users.category);
      setSubCategory(users.subCategory);
      setOther(users.other);
      setPhoneNumber(users.phoneNumber);
      setBirthdate(users.birthdate);
      setMode(1);
    
      setId(users.id);
    }
   
    async function DeleteUser(Id) {
    await axios.delete("https://localhost:7228/api/User/DeleteUser/" + Id);
     alert("User deleted");
     setId("");
     setName("");
     setLastName("");
     setEmail("");
     setPassword("");
     setCategory("");
     setSubCategory("");
     setOther("");
     setPhoneNumber("");
     setBirthdate("");
     Load();
     setMode(0);
    }
   
    async function update(event) {
      event.preventDefault();
      try {
    await axios.patch("https://localhost:7228/api/User/UpdateUser/"+ users.find((u) => u.id === Id).id || Id,
          {
          id: Id,
          name: Name,
          lastName: LastName,
          email: Email,
          password: bcrypt.hashSync(Password,10),
          category: Category,
          subCategory: SubCategory,
          other: Other,
          phoneNumber: PhoneNumber,
          birthdate: Birthdate,

   
          }
        );
        
        alert("User edited");
        setId("");
        setName("");
        setLastName("");
        setEmail("");
        setPassword("");
        setCategory("");
        setSubCategory("");
        setOther("");
        setPhoneNumber("");
        setBirthdate("");
        
      
        Load();
        setMode(0);
      } catch (err) {
        alert(err);
      }
    }


    function ShowUser(currentUser) {
      
      return (
        <>
        <td>{currentUser.id}</td>
        <td>{currentUser.name}</td>
        <td>{currentUser.lastName}</td>
        <td>{currentUser.email}</td>
        <td>{currentUser.password}</td>
        <td>{currentUser.category}</td>
        <td>{currentUser.subCategory}</td>
        <td>{currentUser.other}</td>
        <td>{currentUser.phoneNumber}</td>
        <td>{currentUser.birthdate}</td>
        
    
        </>
        
      );
    }


    async function login(event) {
      event.preventDefault();
      try{
        const result = await axios.get("https://localhost:7228/api/User/LoginUser/"+Email);
       bcrypt.compare(Password,result.data, function(err, isMatch){
        if(err){
          throw err;
        }else if (!isMatch){
          alert("Incorrect credentials");
        }else{
          alert("Logged in");
          setMode(0);
        }
       })

       setEmail("");
       setPassword("");

       Load();
       
      }catch (err){
        alert("Fill all the fields");
      }
    
    }

    function setMode2(event) {
      event.preventDefault();
      setMode(2);
    }

    function setMode0(event) {
      event.preventDefault();
      setMode(0);
      setId("");
      setName("");
      setLastName("");
      setEmail("");
      setPassword("");
      setCategory("");
      setSubCategory("");
      setOther("");
      setPhoneNumber("");
      setBirthdate("");
    }


    return (
      <div>
        <div>
        <h1>CIA DATABASE</h1>

      <div className="container mt-4">

        <form>
          {mode!=2
          ? <>
            <div className="form-group">
            
              <input
                type="text"
                className="form-control"
                id="Id"
                hidden
                value={Id}
                onChange={(event) => {
                  setId(event.target.value);
                }}
              />

              <label>Name </label>
              <input
                type="text"
                className="form-control"
                id="Name"
                value={Name}
                onChange={(event) => {
                  setName(event.target.value);
                }}
              />
            </div>
            <div className="form-group">
              <label>Last Name  </label>
              <input
                type="text"
                className="form-control"
                id="LastName"
                value={LastName}
                onChange={(event) => {
                  setLastName(event.target.value);
                }}
              />
            </div>
            </>
          : <></>
          }
          
          <div className="form-group">
            <label>Email  </label>
            <input
              type="text"
              className="form-control"
              id="Email"
              value={Email}
              onChange={(event) => {
                setEmail(event.target.value);
              }}
            />
          </div>
          <div className="form-group">
            <label>Password </label>
            <input
              type="password"
              className="form-control"
              id="Password"
              value={Password}
              onChange={(event) => {
                setPassword(event.target.value);
              }}
            />
          </div>
          {mode!=2
          ?<>
            <div className="form-group">
            <label>Category </label>
            <input
              type="text"
              className="form-control"
              id="Category"
              value={Category}
              onChange={(event) => {
                setCategory(event.target.value);
              }}
            />
          </div>
          <div className="form-group" >
            {SubCategory==="" ?
            setSubCategory("A"): ""}
            <label>Subcategory  </label>
            <select name="example" id="SubCategory"
              value={SubCategory}
              onChange={(event) => {
                setSubCategory(event.target.value);
              }}>
              <option value="A">A</option>
              <option value="B">B</option>
              <option value="-">Other</option>
            </select>
            
          </div>
          <div className="form-group">
            <label>Other  </label>
            <input
              type="text"
              className="form-control"
              id="Other"
              value={Other}
              onChange={(event) => {
                setOther(event.target.value);
              }}
            />
          </div>
          <div className="form-group">
            <label>Phone Number  </label>
            <input
              type="text"
              className="form-control"
              id="PhoneNumber"
              value={PhoneNumber}
              onChange={(event) => {
                setPhoneNumber(event.target.value);
              }}
            />
          </div>
          <div className="form-group">
            <label>Birthdate  </label>
            <input
              type="text"
              className="form-control"
              id="Birthdate"
              value={Birthdate}
              onChange={(event) => {
                setBirthdate(event.target.value);
              }}
            />
          </div>
          </>
          : <></>
          }
          
    




          <div>
          {mode===0        
          ? <><button className="btn btn-primary mt-4" onClick={save}>Register</button>
            <button className="btn btn-warning mt-4" onClick={setMode2}>Logout</button></>
          : mode===1 ? <><button className="btn btn-warning mt-4" onClick={update}>Update</button>
          <button className="btn btn-warning mt-4" onClick={setMode0}>Cancel</button></>
          : <button className="btn btn-warning mt-4" onClick={login}>Login</button>      }
          </div>
        </form>
      </div>
      <br></br>
 
      <div>
      {showUser ?
      <>
        <table className="table table-dark" align="center">
          <thead>
            <tr>
              <th scope="col">Id</th>
              <th scope="col">Name</th>
              <th scope="col">Last Name</th>
              <th scope="col">Email</th>
              <th scope="col">Password</th>
              <th scope="col">Category</th>
              <th scope="col">Subcategory</th>
              <th scope="col">Other</th>
              <th scope="col">Phone Number</th>
              <th scope="col">Birthdate</th>

        
            </tr>
          </thead>
          <tbody>
          <tr>
                 
           <>{ShowUser(currentUser)}</>
          
          </tr>
        </tbody>
        </table>
        <br></br>
        <div className="container">
          <button     allign="center"
            type="button"
            className="btn btn-warning"
            onClick={() => setShowUser(false)}
          >
            X
          </button></div>
        
        </>
        : <></>}
        
      </div>

      <br></br>
      <br></br>
      <table className="preview" align="center">
        <thead>
          <tr>
            <th scope="col">Name</th>
            <th scope="col">Last Name</th>
            <th scope="col">Phone Number</th>
            <th scope="col">Option</th>
          </tr>
        </thead>
        

        {users.map(function fn(user) {

          return (
            
            <tbody key={user.id}>
              <tr>
                
                <td>{user.name}</td>
                <td>{user.lastName}</td>
                <td>{user.phoneNumber}</td>
                
                <td>
                  <button
                    type="button"
                    className="btn btn-warning"
                    onClick={() => {setShowUser(true);setCurrentUser(user)}}
                  >
                    More
                  </button>
                  {mode!=2
                  ?<>
                    <button
                    type="button"
                    className="btn btn-warning"
                    onClick={() => EditUser(user)}
                  >
                    Edit
                  </button>
                  <button
                    type="button"
                    className="btn btn-danger"
                    onClick={() => DeleteUser(user.id)}
                  >
                    Delete
                  </button>
                  </>
                  :<></>
                  }
                  
                </td>
              </tr>
            </tbody>
          );
        })}
      </table>
        
      </div>
      </div>
    );
  }
  
  export default UserCRUD;
