import axios from "axios";
import { useEffect, useState } from "react";
 

function UserCRUD() {
 
  const [Id, setId] = useState("");
  const [Name, setName] = useState("");
  const [LastName, setLastName] = useState("");
  const [Email, setEmail] = useState("");
  const [Password, setPassword] = useState("");
  const [Category, setCategory] = useState("");
  const [SubCategory, setSubCategory] = useState("");
  const [Other, setOther] = useState("");
  const [PhoneNumber, setPhoneNumber] = useState("");
  const [Birthdate, setBirthdate] = useState("");

  
  var [mode, setMode] = useState(0);
  var [showUser, setShowUser] = useState(true);

  var [users, setUsers] = useState([]);

    useEffect(() => {
      (async () => await Load())();
    }, []);
    
    async function Load() {
      
      const result = await axios.get("https://localhost:7228/api/User/GetUser");
      setUsers(result.data);
      console.log(result.data);
    }
    async function save(event) {
    
      event.preventDefault();
      try {
        await axios.post("https://localhost:7228/api/User/AddUser", {
        name: Name,
        lastName: LastName,
        email: Email,
        password: Password,
        category: Category,
        subCategory: SubCategory,
        other: Other,
        phoneNumber: PhoneNumber,
        birthdate: Birthdate
        
        });
        alert("User added");
        setId("");
        setName("");
        setLastName("");
        setEmail("");
        setPassword("");
        setCategory("");
        setSubCategory("");
        setOther("");
        setPhoneNumber("");
        setBirthdate("");
        
      
        Load();
      } catch (err) {
        alert(err);
      }
    }
   
    async function EditUser(users) {
      setName(users.name);
      setLastName(users.lastName);
      setEmail(users.email);
      setPassword(users.password);
      setCategory(users.category);
      setSubCategory(users.subCategory);
      setOther(users.other);
      setPhoneNumber(users.phoneNumber);
      setBirthdate(users.birthdate);
      setMode(1);
    
      setId(users.id);
    }
   
    async function DeleteUser(Id) {
    await axios.delete("https://localhost:7228/api/User/DeleteUser/" + Id);
     alert("User deleted");
     setId("");
     setName("");
     setLastName("");
     setEmail("");
     setPassword("");
     setCategory("");
     setSubCategory("");
     setOther("");
     setPhoneNumber("");
     setBirthdate("");
     Load();
     setMode(0);
    }
   
    async function update(event) {
      event.preventDefault();
      try {
    await axios.patch("https://localhost:7228/api/User/UpdateUser/"+ users.find((u) => u.id === Id).id || Id,
          {
          Id: Id,
          Name: Name,
          LastName: LastName,
          Email: Email,
          Password: Password,
          Category: Category,
          SubCategory: SubCategory,
          Other: Other,
          PhoneNumber: PhoneNumber,
          Birthdate: Birthdate,

   
          }
        );
        
        alert("User registered");
        setId("");
        setName("");
        setLastName("");
        setEmail("");
        setPassword("");
        setCategory("");
        setSubCategory("");
        setOther("");
        setPhoneNumber("");
        setBirthdate("");
        
      
        Load();
        setMode(0);
      } catch (err) {
        alert(err);
      }
    }


    async function ShowUser(users) {
      setName(users.name);
      setLastName(users.lastName);
      setEmail(users.email);
      setPassword(users.password);
      setCategory(users.category);
      setSubCategory(users.subCategory);
      setOther(users.other);
      setPhoneNumber(users.phoneNumber);
      setBirthdate(users.birthdate);
      setMode(1);
    
      setId(users.id);
    }



    return (
      <div>
        <div>
        <h1>User Details</h1>
      <div className="container mt-4">
        <form>
          <div className="form-group">
          
            <input
              type="text"
              className="form-control"
              id="Id"
              hidden
              value={Id}
              onChange={(event) => {
                setId(event.target.value);
              }}
            />
 
            <label>Name </label>
            <input
              type="text"
              className="form-control"
              id="Name"
              value={Name}
              onChange={(event) => {
                setName(event.target.value);
              }}
            />
          </div>
          <div className="form-group">
            <label>LastName  </label>
            <input
              type="text"
              className="form-control"
              id="LastName"
              value={LastName}
              onChange={(event) => {
                setLastName(event.target.value);
              }}
            />
          </div>
          <div className="form-group">
            <label>Email  </label>
            <input
              type="text"
              className="form-control"
              id="Email"
              value={Email}
              onChange={(event) => {
                setEmail(event.target.value);
              }}
            />
          </div>
          <div className="form-group">
            <label>Password </label>
            <input
              type="text"
              className="form-control"
              id="Password"
              value={Password}
              onChange={(event) => {
                setPassword(event.target.value);
              }}
            />
          </div>
          <div className="form-group">
            <label>Category </label>
            <input
              type="text"
              className="form-control"
              id="Category"
              value={Category}
              onChange={(event) => {
                setCategory(event.target.value);
              }}
            />
          </div>
          <div className="form-group">
            <label>SubCategory  </label>
            <input
              type="text"
              className="form-control"
              id="SubCategory"
              value={SubCategory}
              onChange={(event) => {
                setSubCategory(event.target.value);
              }}
            />
          </div>
          <div className="form-group">
            <label>Other  </label>
            <input
              type="text"
              className="form-control"
              id="Other"
              value={Other}
              onChange={(event) => {
                setOther(event.target.value);
              }}
            />
          </div>
          <div className="form-group">
            <label>PhoneNumber  </label>
            <input
              type="text"
              className="form-control"
              id="PhoneNumber"
              value={PhoneNumber}
              onChange={(event) => {
                setPhoneNumber(event.target.value);
              }}
            />
          </div>
          <div className="form-group">
            <label>Birthdate  </label>
            <input
              type="text"
              className="form-control"
              id="Birthdate"
              value={Birthdate}
              onChange={(event) => {
                setBirthdate(event.target.value);
              }}
            />
          </div>
    




          <div>
          {mode===0        
          ? <button className="btn btn-primary mt-4" onClick={save}>Register</button>
          : <button className="btn btn-warning mt-4" onClick={update}>Update</button>      }
          </div>
        </form>
      </div>
      <br></br>
 
      <div>
        <table className="table table-dark" align="center">
          <thead>
            <tr>
              <th scope="col">Id</th>
              <th scope="col">Name</th>
              <th scope="col">Last Name</th>
              <th scope="col">Email</th>
              <th scope="col">Password</th>
              <th scope="col">Category</th>
              <th scope="col">Sub Category</th>
              <th scope="col">Other</th>
              <th scope="col">Phone Number</th>
              <th scope="col">Birthdate</th>

          
              <th scope="col">Option</th>
            </tr>
          </thead>
        </table>
      </div>

      <br></br>
      <table className="table table-dark" align="center">
        <thead>
          <tr>
            <th scope="col">Id</th>
            <th scope="col">Name</th>
            <th scope="col">Last Name</th>
            <th scope="col">Email</th>
            <th scope="col">Password</th>
            <th scope="col">Category</th>
            <th scope="col">Sub Category</th>
            <th scope="col">Other</th>
            <th scope="col">Phone Number</th>
            <th scope="col">Birthdate</th>

        
            <th scope="col">Option</th>
          </tr>
        </thead>
        <tbody>
          <tr>
          {showUser        
          ? ShowUser(currentUserId)
          : ""     }
          </tr>
        </tbody>

        {users.map(function fn(user) {

          return (
            
            <tbody key={user.id}>
              <tr>
                <th scope="row">{user.id} </th>
                <td>{user.name}</td>
                <td>{user.lastName}</td>
                <td>{user.email}</td>
                <td>{user.password}</td>
                <td>{user.category}</td>
                <td>{user.subCategory}</td>
                <td>{user.other}</td>
                <td>{user.phoneNumber}</td>
                <td>{user.birthdate}</td>
                
                <td>
                
                  <button
                    type="button"
                    className="btn btn-warning"
                    onClick={() => EditUser(user)}
                  >
                    Edit
                  </button>
                  <button
                    type="button"
                    className="btn btn-danger"
                    onClick={() => DeleteUser(user.id)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            </tbody>
          );
        })}
      </table>
        
      </div>
      </div>
    );
  }
  
  export default UserCRUD;
