import axios from "axios";
import { useEffect, useState } from "react";
import bcrypt from 'bcryptjs';
 

function UserCRUD() {
 
  //Input ata storage
  const [Id, setId] = useState("");
  const [Name, setName] = useState("");
  const [LastName, setLastName] = useState("");
  const [Email, setEmail] = useState("");
  const [Password, setPassword] = useState("");
  const [Category, setCategory] = useState("Business");
  const [SubCategory, setSubCategory] = useState("Boss");
  const [Other, setOther] = useState("");
  const [PhoneNumber, setPhoneNumber] = useState("");
  const [Birthdate, setBirthdate] = useState("");



  //View mode(0-register, 1-update, 2-login)
  var [mode, setMode] = useState(2);

  //User details
  var [showUser, setShowUser] = useState(false);
  var [currentUser, setCurrentUser] = useState();

  //Collection of users from db
  var [users, setUsers] = useState([]);

    useEffect(() => {
      (async () => await Load())();
    }, []);
    
    //Get collection of users from db
    async function Load() {
      
      const result = await axios.get("https://localhost:7228/api/User/GetUser");
      setUsers(result.data);
      console.log(result.data);
    }

    //Add user into db
    async function save(event) {
    
      event.preventDefault();

      //Regular expressions taken from the web
      var emailRegex = /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/;
      var pasRegex = new RegExp("^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#\$%\^&\*])(?=.{8,})");

      //These are mine ;)
      var phoneRegex = new RegExp("^[0-9]{9}");
      var dateRegex = new RegExp("^[0-9]{1,2}-[0-9]{1,2}-[0-9]{4}");

      //Boolean to store form state
      var isCorrect=true;

      //String of merged input errors
      var alertInfo="";

      //Local variables
      var SubCategoryX=SubCategory;
      var OtherX=Other;
      

      //Correct categories data 
      if(Category==="Business"){
        alert("Other");
        OtherX="-";
      }else if (Category==="Other"){
        SubCategoryX="-";
        alert("Sub");

      }else if (Category==="Private"){
        SubCategoryX="-";
        OtherX="-";
        alert("Other");
        alert("Sub");

      }
/*
      Category==="" ? setCategory("Business"): setCategory("-");

      SubCategory==="" ? setSubCategory("Boss"): setSubCategory("-");


      if(Other===""){
        setOther("-");
      }*/
      

      //Check if input data are correct
      if(Name===""){
        isCorrect=false;
        alertInfo+="Input your name. ";
      }

      if(LastName===""){
        isCorrect=false;
        alertInfo+="Input your last name. ";
      }

      if(!Email.match(emailRegex)){
        isCorrect=false;
        alertInfo+="Incorrect email form. ";
      }

      if(!Password.match(pasRegex)){
        isCorrect=false;
        alertInfo+="Password to weak. Password must contain at least 1 lowercase alphabetical character, 1 uppercase alphabetical character, 1 numeric character, 1 special character, and be 8 characters or longer. ";
      }

      if(!PhoneNumber.match(phoneRegex)){
        isCorrect=false;
        alertInfo+="Incorrect phone number. ";
      }

      if(!Birthdate.match(dateRegex)){
        isCorrect=false;
        alertInfo+="Incorrect date form. ";
      }

      try {
        if(isCorrect){
          const result = await axios.post("https://localhost:7228/api/User/AddUser", {
            name: Name,
            lastName: LastName,
            email: Email,
            password: bcrypt.hashSync(Password,10),
            category: Category,
            subCategory: SubCategoryX,
            other: OtherX,
            phoneNumber: PhoneNumber,
            birthdate: Birthdate
            
            });

            //Checkng if given email already exists (gets response from the server)
            if(result.data!=""){
              alert("User added");
            }else{
              alert("Given email already exsts");
            }
            
            //Clearing input fields and setting defaults
            setId("");
            setName("");
            setLastName("");
            setEmail("");
            setPassword("");
            setCategory("Business");
            setSubCategory("Boss");
            setOther("");
            setPhoneNumber("");
            setBirthdate("");
            //alert(alertInfo);
        }else{

          //Shows errors if input is not correct
          alert(alertInfo);
        }
      
        Load();
      } catch (err) {
        alert(err);
      }
    }
   
    //Fills input fields with selected user data
    async function EditUser(users) {
      setName(users.name);
      setLastName(users.lastName);
      setEmail(users.email);
      setPassword(users.password);
      setCategory(users.category);
      setSubCategory(users.subCategory);
      setOther(users.other);
      setPhoneNumber(users.phoneNumber);
      setBirthdate(users.birthdate);
      setMode(1);
    
      setId(users.id);
    }
   
    //Delete user
    async function DeleteUser(Id) {
    await axios.delete("https://localhost:7228/api/User/DeleteUser/" + Id);
     alert("User deleted");
     setId("");
     setName("");
     setLastName("");
     setEmail("");
     setPassword("");
     setCategory("Business");
     setSubCategory("Boss");
     setOther("");
     setPhoneNumber("");
     setBirthdate("");
     Load();
     setMode(0);
    }
   
    //Same as save but edits user (could have done same functon with different conditions)
    async function update(event) {
      event.preventDefault();

      var emailRegex = /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/;
      var pasRegex = new RegExp("^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#\$%\^&\*])(?=.{8,})");
      var phoneRegex = new RegExp("^[0-9]{9}");
      var dateRegex = new RegExp("^[0-9]{1,2}-[0-9]{1,2}-[0-9]{4}");

      var isCorrect=true;
      var alertInfo="";

      var SubCategoryX=SubCategory;
      var OtherX=Other;
      
      if(Category==="Business"){
        alert("Other");
        OtherX="-";
      }else if (Category==="Other"){
        SubCategoryX="-";
        alert("Sub");

      }else if (Category==="Private"){
        SubCategoryX="-";
        OtherX="-";
        alert("Other");
        alert("Sub");

      }
      
      if(Name===""){
        isCorrect=false;
        alertInfo+="Input your name. ";
      }

      if(LastName===""){
        isCorrect=false;
        alertInfo+="Input your last name. ";
      }

      if(!Email.match(emailRegex)){
        isCorrect=false;
        alertInfo+="Incorrect email form. ";
      }

      if(!Password.match(pasRegex)){
        isCorrect=false;
        alertInfo+="Password to weak. Password must contain at least 1 lowercase alphabetical character, 1 uppercase alphabetical character, 1 numeric character, 1 special character, and be 8 characters or longer. ";
      }

      if(!PhoneNumber.match(phoneRegex)){
        isCorrect=false;
        alertInfo+="Incorrect phone number. ";
      }

      if(!Birthdate.match(dateRegex)){
        isCorrect=false;
        alertInfo+="Incorrect date form. ";
      }

      try {
        if(isCorrect){

          const result = await axios.patch("https://localhost:7228/api/User/UpdateUser/"+ users.find((u) => u.id === Id).id || Id,
          {
          id: Id,
          name: Name,
          lastName: LastName,
          email: Email,
          password: bcrypt.hashSync(Password,10),
          category: Category,
          subCategory: SubCategory,
          other: Other,
          phoneNumber: PhoneNumber,
          birthdate: Birthdate,

   
          }
        );

        setShowUser(false);


        alert("User edited");
        setId("");
        setName("");
        setLastName("");
        setEmail("");
        setPassword("");
        setCategory("Business");
        setSubCategory("Boss");
        setOther("");
        setPhoneNumber("");
        setBirthdate("");
        setMode(0);


        }else{
          alert(alertInfo);
        }
    
      
        Load();
        
      } catch (err) {
        alert(err);
      }
    }

    //Shows user details
    function ShowUser(currentUser) {
      
      return (
        <>
        <td>{currentUser.id}</td>
        <td>{currentUser.name}</td>
        <td>{currentUser.lastName}</td>
        <td>{currentUser.email}</td>
        <td>{currentUser.password}</td>
        <td>{currentUser.category}</td>
        <td>{currentUser.subCategory}</td>
        <td>{currentUser.other}</td>
        <td>{currentUser.phoneNumber}</td>
        <td>{currentUser.birthdate}</td>
        
    
        </>
        
      );
    }

    //Login user  
    async function login(event) {
      event.preventDefault();
      try{
        const result = await axios.get("https://localhost:7228/api/User/LoginUser/"+Email);
       bcrypt.compare(Password,result.data, function(err, isMatch){
        if(err){
          throw err;
        }else if (!isMatch){
          alert("Incorrect credentials");
        }else{
          alert("Logged in");
          setMode(0);
        }
       })

       setEmail("");
       setPassword("");

       Load();
       
      }catch (err){
        alert("Fill all the fields");
      }
    
    }

    //Set login view mode
    function setMode2(event) {
      event.preventDefault();
      setMode(2);
    }

    //Set register view mode
    function setMode0(event) {
      event.preventDefault();
      setMode(0);
      setId("");
      setName("");
      setLastName("");
      setEmail("");
      setPassword("");
      setCategory("Business");
      setSubCategory("Boss");
      setOther("");
      setPhoneNumber("");
      setBirthdate("");
    }
/*
    function checkCategory() {
      if(Category==="Business"){
        setOther("-");
      }else if (Category==="Other"){
        setSubCategory("-");
      }else if (Category==="Private"){
        setSubCategory("-");
        setOther("-");
      }

      console.log(Category+" "+SubCategory+" "+Other);
    }*/


    return (
      <div>
        <div>
        <h1>CIA DATABASE</h1>
        {/* Data input fields */}
      <div className="container mt-4">
      <table>
        <form>
          {mode!=2
          ? <>
            <tr>
              <td>
              <label>Name </label>
            <div className="form-group">
            
              <input
                type="text"
                className="form-control"
                id="Id"
                hidden
                value={Id}
                onChange={(event) => {
                  setId(event.target.value);
                }}
              />

              
              <input
                type="text"
                className="form-control"
                id="Name"
                value={Name}
                onChange={(event) => {
                  setName(event.target.value);
                }}
              />
            </div>
            </td>
            </tr>
            <div className="form-group">
              <label>Last Name  </label>
              <input
                type="text"
                className="form-control"
                id="LastName"
                value={LastName}
                onChange={(event) => {
                  setLastName(event.target.value);
                }}
              />
            </div>
            </>
          : <></>
          }
          
          <div className="form-group">
            <label>Email  </label>
            <input
              type="text"
              className="form-control"
              id="Email"
              value={Email}
              onChange={(event) => {
                setEmail(event.target.value);
              }}
            />
          </div>
          <div className="form-group">
            <label>Password </label>
            <input
              type="password"
              className="form-control"
              id="Password"
              value={Password}
              onChange={(event) => {
                setPassword(event.target.value);
              }}
            />
          </div>
          {mode!=2
          ?<>
            <div className="form-group">
              



              <label>Category </label>
              <select name="example" id="Category"
                value={Category}
                onChange={(event) => {
                  setCategory(event.target.value);
                  //checkCategory();
                }}>
                <option value="Business">Business</option>
                <option value="Private">Private</option>
                <option value="Other">Other</option>
              </select>
          </div>

          {Category==="Business" ?
            <>

            <div className="form-group" >
              

              <label>Subcategory  </label>
              <select name="example" id="SubCategory"
                value={SubCategory}
                onChange={(event) => {
                  setSubCategory(event.target.value);
                  //checkCategory();
                }}>
                <option value="Boss">Boss</option>
                <option value="Employee">Employee</option>
                <option value="Customer">Customer</option>
              </select>
              
            </div>

            

            

            </>
            :<></>
          }

          {Category==="Other" ?
            <>
            
            <div className="form-group">
              <label>Other  </label>
              <input
                type="text"
                className="form-control"
                id="Other"
                value={Other}
                onChange={(event) => {
                  setOther(event.target.value);
                  //checkCategory();
                }}
              />
            </div>
            
            </>
          : <></>
          }
          
          <div className="form-group">
            <label>Phone Number  </label>
            <input
              placeholder="123456789"
              type="text"
              className="form-control"
              id="PhoneNumber"
              value={PhoneNumber}
              onChange={(event) => {
                setPhoneNumber(event.target.value);
              }}
            />
          </div>
          <div className="form-group">
            <label>Birthdate  </label>
            <input
              placeholder="DD-MM-YYYY"
              type="text"
              className="form-control"
              id="Birthdate"
              value={Birthdate}
              onChange={(event) => {
                setBirthdate(event.target.value);
              }}
            />
          </div>
          </>
          : <></>
          }
          
    




          <div>
          {mode===0        
          ? <><button className="btn btn-primary mt-4" onClick={save}>Register</button>
            <button className="btn btn-warning mt-4" onClick={setMode2}>Logout</button></>
          : mode===1 ? <><button className="btn btn-warning mt-4" onClick={update}>Update</button>
          <button className="btn btn-warning mt-4" onClick={setMode0}>Cancel</button></>
          : <button className="btn btn-warning mt-4" onClick={login}>Login</button>      }
          </div>
        </form>
      </table>
      </div>
      <br></br>
 

      {/* User details */}
      <div>
      {showUser ?
      <>
        <table className="table table-dark" align="center">
          <thead>
            <tr>
              <th scope="col">Id</th>
              <th scope="col">Name</th>
              <th scope="col">Last Name</th>
              <th scope="col">Email</th>
              <th scope="col">Password</th>
              <th scope="col">Category</th>
              <th scope="col">Subcategory</th>
              <th scope="col">Other</th>
              <th scope="col">Phone Number</th>
              <th scope="col">Birthdate</th>

        
            </tr>
          </thead>
          <tbody>
          <tr>
                 
           <>{ShowUser(currentUser)}</>
          
          </tr>
        </tbody>
        </table>
        <br></br>
        <div className="container">
          <button     allign="center"
            type="button"
            className="btn btn-warning"
            onClick={() => setShowUser(false)}
          >
            X
          </button></div>
        
        </>
        : <></>}
        
      </div>

      <br></br>
      <br></br>
      {/* Users table */}
      <table className="preview" align="center">
        <thead>
          <tr>
            <th scope="col">Name</th>
            <th scope="col">Last Name</th>
            <th scope="col">Phone Number</th>
            <th scope="col">Option</th>
          </tr>
        </thead>
        

        {users.map(function fn(user) {

          return (
            
            <tbody key={user.id}>
              <tr>
                
                <td>{user.name}</td>
                <td>{user.lastName}</td>
                <td>{user.phoneNumber}</td>
                
                <td>
                  <button
                    type="button"
                    className="btn btn-warning"
                    onClick={() => {setShowUser(true);setCurrentUser(user)}}
                  >
                    More
                  </button>
                  {mode!=2
                  ?<>
                    <button
                    type="button"
                    className="btn btn-warning"
                    onClick={() => EditUser(user)}
                  >
                    Edit
                  </button>
                  <button
                    type="button"
                    className="btn btn-danger"
                    onClick={() => DeleteUser(user.id)}
                  >
                    Delete
                  </button>
                  </>
                  :<></>
                  }
                  
                </td>
              </tr>
            </tbody>
          );
        })}
      </table>
        
      </div>
      </div>
    );
  }
  
  export default UserCRUD;
